-- =====================================================
-- Grip & Grin Complete Database Schema
-- =====================================================

-- Drop existing tables if they exist
DROP TABLE IF EXISTS articles;
DROP TABLE IF EXISTS categories;
DROP TABLE IF EXISTS users;

-- =====================================================
-- USERS TABLE (Authentication with Roles)
-- =====================================================
CREATE TABLE `users` (
                         `id` INT AUTO_INCREMENT PRIMARY KEY,
                         `username` VARCHAR(50) NOT NULL UNIQUE,
                         `email` VARCHAR(100) NOT NULL UNIQUE,
                         `password_hash` VARCHAR(255) NOT NULL,
                         `role` ENUM('admin', 'editor', 'user') NOT NULL DEFAULT 'user',
                         `is_active` BOOLEAN NOT NULL DEFAULT TRUE,
                         `email_verified` BOOLEAN NOT NULL DEFAULT FALSE,
                         `email_verification_token` VARCHAR(255) NULL,
                         `password_reset_token` VARCHAR(255) NULL,
                         `password_reset_expires` DATETIME NULL,
                         `first_name` VARCHAR(50) NULL,
                         `last_name` VARCHAR(50) NULL,
                         `bio` TEXT NULL,
                         `avatar_path` VARCHAR(255) NULL,
                         `created_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                         `updated_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
                         INDEX `idx_users_email` (`email`),
                         INDEX `idx_users_username` (`username`),
                         INDEX `idx_users_role` (`role`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

-- =====================================================
-- CATEGORIES TABLE
-- =====================================================
CREATE TABLE `categories` (
                              `id` INT AUTO_INCREMENT PRIMARY KEY,
                              `name` VARCHAR(50) NOT NULL UNIQUE,
                              `slug` VARCHAR(50) NOT NULL UNIQUE,
                              `description` TEXT,
                              `created_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                              `updated_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

-- =====================================================
-- ARTICLES TABLE (with Image Support)
-- =====================================================
CREATE TABLE `articles` (
                            `id` INT AUTO_INCREMENT PRIMARY KEY,
                            `title` VARCHAR(255) NOT NULL,
                            `slug` VARCHAR(255) NOT NULL UNIQUE,
                            `content` TEXT NOT NULL,
                            `excerpt` VARCHAR(500) NULL,
                            `author_id` INT NOT NULL,
                            `category_id` INT NOT NULL,
                            `status` ENUM('draft', 'published', 'archived') NOT NULL DEFAULT 'draft',
                            `published_at` DATETIME NULL,
                            `created_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                            `updated_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
                            `image_original_path` VARCHAR(255) NULL,
                            `image_thumbnail_path` VARCHAR(255) NULL,
                            `image_medium_path` VARCHAR(255) NULL,
                            `image_full_path` VARCHAR(255) NULL,
                            `image_alt_text` VARCHAR(255) NULL,
                            `image_width` INT NULL,
                            `image_height` INT NULL,
                            FOREIGN KEY (`author_id`) REFERENCES `users`(`id`),
                            FOREIGN KEY (`category_id`) REFERENCES `categories`(`id`),
                            INDEX `idx_articles_slug` (`slug`),
                            INDEX `idx_articles_published` (`published_at`),
                            INDEX `idx_articles_category` (`category_id`),
                            INDEX `idx_articles_status` (`status`),
                            INDEX `idx_articles_author` (`author_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

