-- Verify data insertion
SELECT 'Users' as table_name, COUNT(*) as count FROM users
UNION ALL
SELECT 'Categories' as table_name, COUNT(*) as count FROM categories
UNION ALL
SELECT 'Articles' as table_name, COUNT(*) as count FROM articles;

-- Verify category distribution
SELECT c.name as category, c.slug, COUNT(a.id) as article_count
FROM categories c
         LEFT JOIN articles a ON c.id = a.category_id
GROUP BY c.id, c.name, c.slug
ORDER BY c.id;

-- Verify user roles
SELECT role, COUNT(*) as count FROM users GROUP BY role;

-- Verify articles have images
SELECT
    COUNT(*) as total_articles,
    COUNT(image_thumbnail_path) as articles_with_images,
    ROUND((COUNT(image_thumbnail_path) / COUNT(*)) * 100, 1) as percentage_with_images
FROM articles;
