    ];

    public function getFunctions(): array
    {
        return [
            new TwigFunction('icon', [$this, 'renderIcon'], ['is_safe' => ['html']]),
        ];
    }

    public function renderIcon(string $name, string $class = 'icon', array $attributes = []): string
    {
        if (!isset($this->icons[$name])) {
            return '<!-- Icon not found: ' . htmlspecialchars($name) . ' -->';
        }

        $svg = $this->icons[$name];

        // Add class attribute
        if ($class) {
            $svg = str_replace('<svg', '<svg class="' . htmlspecialchars($class) . '"', $svg);
        }

        // Add additional attributes
        foreach ($attributes as $attr => $value) {
            $svg = str_replace('<svg', '<svg ' . htmlspecialchars($attr) . '="' . htmlspecialchars($value) . '"', $svg);
        }

        return $svg;
    }
}
